import React from 'react';
import { Typography, Button, ButtonGroup, Container } from '@mui/material';
import AcUnitOutlinedIcon from '@mui/icons-material/AcUnitOutlined';
import SendIcon from '@mui/icons-material/Send';
import KeyboardArrowRightIcon from '@mui/icons-material/KeyboardArrowRight';
import { styled } from '@mui/material/styles';

// Create a custom styled Button component
const CustomButton = styled(Button)({
  fontSize: 60,
  color: 'white',
  backgroundColor: 'violet',
  '&:hover': {
    backgroundColor: 'blue',
  },
});

// Define a custom class for the Typography component
const customTitleClass = {
  textDecoration: 'underline',
  marginBottom: 20,
};

export default function Create() {
  return (
    <Container>
      <Typography
        sx={customTitleClass} // Use sx prop to apply custom styles
        variant="h6"
        color="textSecondary"
        component="h2"
        gutterBottom
      >
        Create a new Note
      </Typography>

      <CustomButton
        type="submit"
        color="secondary"
        variant="outlined"
        startIcon={<SendIcon />} // left size
        endIcon={<KeyboardArrowRightIcon />} // right size
      >
        Submit
      </CustomButton>
    </Container>
  );
}
