import { AppBar, Container, Drawer, List, ListItem, ListItemIcon, ListItemText, Toolbar, Typography } from '@mui/material'
import React from 'react'
import { AddCircleOutlineOutlined, SubjectOutlined } from '@mui/icons-material';
import { useHistory, useLocation } from 'react-router-dom';


const drawerWidth = 200;

export default function Layout({ children }) {

  const useStyles ={
    page: {
      background: '#f9f9f9',
      width: '100%',
      //padding: theme.spacing(3),
    },
    root: {
      display: 'flex',
    },
    drawer: {
      width: drawerWidth,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    active: {
      background: '#f4f4f4'
    },
    title: { 
      //padding: theme.spacing(2),
    },
    AppBar: {
      width: `clac(100% - ${drawerWidth}px)`
    } 
};

  const history = useHistory()
  const location = useLocation()

  const menuItems = [
    { 
      text: 'My Notes', 
      icon: <SubjectOutlined color="secondary" />, 
      path: '/' 
    },
    { 
      text: 'Create Note', 
      icon: <AddCircleOutlineOutlined color="secondary" />, 
      path: '/create' 
    },
  ];

  return (
    <div sx={useStyles.root}>
      {/* app bar */}
      <AppBar sx={useStyles.AppBar}>
        <Toolbar>
          <Typography>
            welcome to the ninja note welcome
          </Typography>
        </Toolbar>
      </AppBar>


      {/* side drawer */}
      <Drawer
        sx={useStyles.drawer}
        variant="permanent"
        sx={useStyles.drawerPaper}
        anchor="left"
      >
        <div>
          <Typography variant="h5" sx={useStyles.title}>
            Ninja Notes
          </Typography>
        </div>

        {/* links/list section */}
        <List>
          {menuItems.map((item) => (
            <ListItem 
              button 
              key={item.text} 
              onClick={() => history.push(item.path)}
              sx={useStyles.location}
              className={location.pathname == item.path ? classes.active : null}
            >
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
        </List>

      </Drawer>

      {/* main content */}
      <div sx={useStyles.page}>
        { children }
      </div>
    </div>
  )
}
