import { AppBar, Container, Drawer, List, ListItem, ListItemIcon, ListItemText, ThemeProvider,createTheme, Toolbar, Typography } from '@mui/material'
import React from 'react'
import { makeStyles , styled } from '@mui/styles';
import { AddCircleOutlineOutlined, SubjectOutlined } from '@mui/icons-material';
import { useHistory, useLocation } from 'react-router-dom';

const theme = createTheme(); 
const drawerWidth = 200;

export default function Layout({ children }) {

  const styles = {
    page: {
      background: '#f9f9f9',
      width: '100%',
      padding: theme.spacing(3),
    },
    root: {
      display: 'flex',
    },
    drawer: {
      width: drawerWidth,
    },
    drawerPaper: {
      width: drawerWidth,
    },
    active: {
      background: '#f4f4f4'
    },
    title: { 
      padding: theme.spacing(2),
    },
    appBar: {
      //width: `clac(100% - ${drawerWidth}px)`
      width: 140,
    }
  }

  const history = useHistory()
  const location = useLocation()

  const menuItems = [
    { 
      text: 'My Notes', 
      icon: <SubjectOutlined color="secondary" />, 
      path: '/' 
    },
    { 
      text: 'Create Note', 
      icon: <AddCircleOutlineOutlined color="secondary" />, 
      path: '/create' 
    },
  ];

  return (
    <div sx = {styles.root}>
      {/* app bar */}
      <AppBar 
      sx = {styled.appBar}
      >
        <Toolbar>
          <Typography>
            welcome to the ninja note welcome
          </Typography>
        </Toolbar>
      </AppBar>


      {/* side drawer */}
      <Drawer
        sx = {styled.drawer}
        variant="permanent"
        //classes={{ paper: classes.drawerPaper }}
        //paper:sx = {styled.paper}
        anchor="left"
      >
        <div>
          <Typography variant="h5" sx = {styled.title}>
            Ninja Notes
          </Typography>
        </div>

        {/* links/list section */}
        <List>
          {menuItems.map((item) => (
            <ListItem 
              button 
              key={item.text} 
              onClick={() => history.push(item.path)}
              //className={location.pathname == item.path ? classes.active : null}
            >
              <ListItemIcon>{item.icon}</ListItemIcon>
              <ListItemText primary={item.text} />
            </ListItem>
          ))}
        </List>

      </Drawer>

      {/* main content */}
      <div sx = {styled.page}>
        { children }
      </div>
    </div>
  )
}
